mapper.select:
SELECT 

FROM base_news a

WHERE a.id = #{id}

SELECT 

FROM base_news a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}


		AND a.title LIKE 
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			concat('%',#{title},'%')


		AND a.user = #{user.id}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM base_news a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_news SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
