mapper.select:
SELECT 

FROM base_novel a

WHERE a.id = #{id}

SELECT
FROM base_novel a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
ORDER BY ${page.orderBy}
ORDER BY a.update_date DESC

SELECT 

FROM base_novel a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE base_novel SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
